    public static boolean checkIfIsEnough(int requested, int available) {
        if (requested < available) {
            return true;
        } else {
            return false;
        }
    }

    public static boolean checkNoOfCups() {
        if (noOfCups >= 1) {
            return true;
        } else {
            return false;
        }
    }


    public static void buy(int typeOfCoffee) {
        switch(typeOfCoffee){
            case 1:
            int requestedWater = 250;
            int requestedCoffee = 16;
            int price = 4;
            boolean checkWater = checkIfIsEnough(requestedWater, waterQuantity);
            boolean checkCoffe = checkIfIsEnough(requestedCoffee, coffeeQuantity);
            boolean checkCups = checkNoOfCups();
            if ((checkWater == true) && (checkCoffe == true) && (checkCups == true) ) {
                waterQuantity = waterQuantity - requestedWater;
                coffeeQuantity = coffeeQuantity - requestedCoffee;
                noOfCups = noOfCups - 1;
                amountOfMoney = amountOfMoney + price;
                System.out.println("na-ti cafeaua ta");
            }
            case 2:
            requestedWater = 350;
            int requestedMilk = 75;
            requestedCoffee = 20;
            int priceLatte = 7;
            checkWater = checkIfIsEnough(requestedWater, waterQuantity);
            boolean checkMilk = checkIfIsEnough(requestedMilk, milkQuantity);
            checkCoffe = checkIfIsEnough(requestedCoffee, coffeeQuantity);
            if (checkWater && checkCoffe && checkMilk && checkNoOfCups()) {
                waterQuantity = waterQuantity - requestedWater;
                milkQuantity = milkQuantity - requestedMilk;
                coffeeQuantity = coffeeQuantity - requestedCoffee;
                noOfCups = noOfCups - 1;
                amountOfMoney = amountOfMoney + priceLatte;
                System.out.println("na-ti cafeaua ta lateu");
            }
            case 3:
                requestedWater = 200;
                requestedMilk = 100;
                requestedCoffee = 12;
                int priceCap = 6;
                checkWater = checkIfIsEnough(requestedWater, waterQuantity);
                checkMilk = checkIfIsEnough(requestedMilk, milkQuantity);
                checkCoffe = checkIfIsEnough(requestedCoffee, coffeeQuantity);
                if (checkWater && checkCoffe && checkMilk && checkNoOfCups()) {
                    waterQuantity = waterQuantity - requestedWater;
                    milkQuantity = milkQuantity - requestedMilk;
                    coffeeQuantity = coffeeQuantity - requestedCoffee;
                    noOfCups = noOfCups - 1;
                    amountOfMoney = amountOfMoney + priceCap;
                    System.out.println("na-ti cafeaua ta capucino");
                }
            default:
                System.out.println("Some ingredients missing");
        }
    }
}

import machine.CoffeeMachine;
import org.hyperskill.hstest.stage.StageTest;
import org.hyperskill.hstest.testcase.CheckResult;
import org.hyperskill.hstest.testcase.TestCase;

import java.util.ArrayList;
import java.util.List;


class TestClue {
    int water;
    int milk;
    int beans;
    int cups;
    int money;
    String feedback;
    TestClue(int w, int m, int b, int c, int mo, String feedback) {
        water = w;
        milk = m;
        beans = b;
        cups = c;
        money = mo;
        this.feedback = feedback;
    }
}

public class CoffeeMachineTest extends StageTest<TestClue> {

    public CoffeeMachineTest() {
        super(CoffeeMachine.class);
    }

    @Override
    public List<TestCase<TestClue>> generate() {
        return List.of(
            new TestCase<TestClue>()
                .setAttach(new TestClue(
                700 - 400,
                390 - 540,
                80 - 120 ,
                7 - 9,
                0 - 550,
                    "This test is exactly " +
                        "like in the example - try to run it by yourself"))
