                .setInput(
                    "remaining\n" +
                    "buy\n" +
                    "back\n" +
                    "remaining\n" +
                    "exit\n")
        );
    }

    @Override
    public CheckResult check(String reply, TestClue clue) {
        String[] lines = reply.split("\\n");

        if (lines.length <= 1) {
            return CheckResult.wrong("Looks like you didn't print anything!");
        }

        int water_ = clue.water;
        int milk_ = clue.milk;
        int beans_ = clue.beans;
        int cups_ = clue.cups;
        int money_ = clue.money;

        List<Integer> milk = new ArrayList<>();
        List<Integer> water = new ArrayList<>();
        List<Integer> beans = new ArrayList<>();
        List<Integer> cups = new ArrayList<>();
        List<Integer> money = new ArrayList<>();

        for (String line : lines) {
            line = line.replace("$", "").trim();
            String[] words = line.split("\\s+");
            if (words.length == 0) {
                continue;
            }
            String firstWord = words[0];
            int amount;
            try {
                amount = Integer.parseInt(firstWord);
            }
            catch (Exception e) {
                continue;
            }
            if (line.contains("milk")) {
                milk.add(amount);
            }
            else if (line.contains("water")) {
                water.add(amount);
            }
            else if (line.contains("beans")) {
                beans.add(amount);
            }
            else if (line.contains("cups")) {
                cups.add(amount);
            }
            else if (line.contains("money")) {
                money.add(amount);
            }
        }

        if (milk.size() != 2) {
            return new CheckResult(false,
                "There should be two lines with \"milk\", " +
                    "found: " + milk.size());
        }

        if (water.size() != 2) {
            return new CheckResult(false,
                "There should be two lines with \"water\", " +
                    "found: " + water.size());
        }

        if (beans.size() != 2) {
            return new CheckResult(false,
                "There should be two lines with \"beans\", " +
                    "found: " + beans.size());
        }

        if (cups.size() != 2) {
            return new CheckResult(false,
                "There should be two lines with \"cups\", " +
                    "found: " + cups.size());
        }

        if (money.size() != 2) {
            return new CheckResult(false,
                "There should be two lines with \"money\", " +
                    "found: " + money.size());
        }

        int milk0 = milk.get(0);
        int milk1 = milk.get(milk.size() - 1);

        int water0 = water.get(0);
        int water1 = water.get(water.size() - 1);

        int beans0 = beans.get(0);
        int beans1 = beans.get(beans.size() - 1);

        int cups0 = cups.get(0);
        int cups1 = cups.get(cups.size() - 1);

        int money0 = money.get(0);
        int money1 = money.get(money.size() - 1);

        int diffWater = water1 - water0;
        int diffMilk = milk1 - milk0;
        int diffBeans = beans1 - beans0;
        int diffCups = cups1 - cups0;
        int diffMoney = money1 - money0;

        boolean isCorrect =
            diffWater == water_ &&
            diffMilk == milk_ &&
            diffBeans == beans_ &&
            diffCups == cups_ &&
            diffMoney == money_;

        return new CheckResult(isCorrect, clue.feedback);
    }
}

package machine;

import java.util.Scanner;

public class CoffeeMachine {
     public static int waterQuantity = 400;
    public static int milkQuantity = 540;
    public static int coffeeQuantity = 120;
    public static int noOfCups = 9;

    public static int amountOfMoney = 550;

    public static void main(String[] args) {
        int userOption = 0;
        Scanner scan = new Scanner(System.in);

        while (true) {
            System.out.println("type buy / fill / take / exit");
            String userOptionString = scan.next();

            if (userOptionString.equals("buy")) {
                userOption = 1;
            } else if (userOptionString.equals("fill")) {
                userOption = 2;
            } else if (userOptionString.equals("take")) {
                userOption = 3;
            } else if(userOptionString.equals("remaining")) {
                userOption = 4;
            }  else if(userOptionString.equals("exit")){
                userOption = 5;
            }
            else {
                System.out.println("nu bun tastat buy / fill / take/ remaining / exit");
            }


            if (userOption == 1) {
                System.out.println("type 1 for espresso, 2 for latte, 3 for cappuccino, back to go back");
                int userChoice = 0;
                String userChoiceString = scan.next();
                if (userChoiceString.equals("1")) {
                   userChoice = 1;
                } else if (userChoiceString.equals("2")) {
                    userChoice = 2;
                } else if (userChoiceString.equals("3")) {
                    userChoice = 3;
                }  else if(userChoiceString.equals("back")){
                    userChoice = 4;
                }
                else {
                    System.out.println("nu bun tastat 1/ 2/3 back");
                }

                buy(userChoice);

            }
            if (userOption == 2) {
                System.out.println("How much water you want to add?");
                waterQuantity += scan.nextInt();
                System.out.println("ete atata apa");
                System.out.println("How much milk you want to add?");
                milkQuantity += scan.nextInt();
                System.out.println("ete atata lapte");
                System.out.println("How much coffe you want to add?");
                coffeeQuantity += scan.nextInt();
                System.out.println("ete atata cafea");
                System.out.println("How many cups you want to add?");
                noOfCups += scan.nextInt();
                System.out.println("ete atatea pahare");
            }
            if (userOption == 3) {
                System.out.println("I gave you $" + amountOfMoney);
                amountOfMoney = 0;
            }
            if(userOption == 4){
                printTheStatus();
            }
            if (userOption == 5) {
                break;
            }
        }
    }

    public static boolean checkIfIsEnough(int requested, int available) {
        if (requested < available) {
            return true;
        } else {
            return false;
        }
    }

    public static boolean checkNoOfCups() {
        if (noOfCups >= 1) {
            return true;
        } else {
            return false;
        }
    }


    public static void buy(int userChoice) {
        switch(userChoice){
            case 1:
                int requestedWater = 250;
                int requestedCoffee = 16;
                int price = 4;
                boolean checkWater = checkIfIsEnough(requestedWater, waterQuantity);
                boolean checkCoffe = checkIfIsEnough(requestedCoffee, coffeeQuantity);
                boolean checkCups = checkNoOfCups();
                if ((checkWater == true) && (checkCoffe == true) && (checkCups == true) ) {
                    waterQuantity = waterQuantity - requestedWater;
                    coffeeQuantity = coffeeQuantity - requestedCoffee;
                    noOfCups = noOfCups - 1;
                    amountOfMoney = amountOfMoney + price;
                    System.out.println("na-ti cafeaua ta");
                }else{
                    System.out.println("Some ingredients missing");
                }
                break;
            case 2:
                requestedWater = 350;
                int requestedMilk = 75;
                requestedCoffee = 20;
                int priceLatte = 7;
                checkWater = checkIfIsEnough(requestedWater, waterQuantity);
                boolean checkMilk = checkIfIsEnough(requestedMilk, milkQuantity);
                checkCoffe = checkIfIsEnough(requestedCoffee, coffeeQuantity);
                if (checkWater && checkCoffe && checkMilk && checkNoOfCups()) {
                    waterQuantity = waterQuantity - requestedWater;
                    milkQuantity = milkQuantity - requestedMilk;
                    coffeeQuantity = coffeeQuantity - requestedCoffee;
                    noOfCups = noOfCups - 1;
                    amountOfMoney = amountOfMoney + priceLatte;
                    System.out.println("na-ti cafeaua ta lateu");
                }else{
                    System.out.println("Some ingredients missing");
                }
                break;
            case 3:
                requestedWater = 200;
                requestedMilk = 100;
                requestedCoffee = 12;
                int priceCap = 6;
                checkWater = checkIfIsEnough(requestedWater, waterQuantity);
                checkMilk = checkIfIsEnough(requestedMilk, milkQuantity);
                checkCoffe = checkIfIsEnough(requestedCoffee, coffeeQuantity);
                if (checkWater && checkCoffe && checkMilk && checkNoOfCups()) {
                    waterQuantity = waterQuantity - requestedWater;
                    milkQuantity = milkQuantity - requestedMilk;
                    coffeeQuantity = coffeeQuantity - requestedCoffee;
                    noOfCups = noOfCups - 1;
                    amountOfMoney = amountOfMoney + priceCap;
                    System.out.println("na-ti cafeaua ta capucino");
                }else{
                    System.out.println("Some ingredients missing");
                }
                break;
            case 4:
                break;
        }
        }


    public static void printTheStatus(){
        System.out.println("The coffee machine has:");
        System.out.println(waterQuantity + " of water");
        System.out.println(milkQuantity + " of milk");
        System.out.println(coffeeQuantity + " of coffee beans");
        System.out.println(noOfCups + " of disposable cups");
        System.out.println(amountOfMoney + " of money");
    }
}

